source <(curl -s https://gitlab.com/nstoc6696/Gitpod-VNC/-/raw/master/updater.sh) && alias downloadhelp='clear && echo "to set downloads, go to google settings (by clicking the 3 lines in top right) click settings, then search for downloads and click on browse. Navigate to the root directory (/), then set downloads to /workspace/Gitpod-VNC/ and hit open, sometimes it may not update, so you have to browse and open the folder again."' && alias chrome='clear && google-chrome' && echo 'Type "downloadhelp" for help with downloading in chrome.' && echo "Type chrome to start chrome." && echo 'The chrome command will be unavailable until Backend: sudo has finished installing.'
./setup.sh
